app.controller("TimerController",["$scope","$interval",function(r,t){r.size=300,r.progress=1,r.strokeWidth=20,r.stroke="#fff",r.counterClockwise=!0,r.counter=1500,r.initVal=1500,r.takenPomodoros=0,r.takenShortBreaks=0,r.takenLongBreaks=0,r.current="pomodoro",r.start=function(){r.stop(),timer=t(function(){if(r.counter--,r.progress=r.counter/r.initVal,0===r.counter){r.stop(),r.progress=1;var t=new Audio("assets/sound/bell.mp3");t.play(),r.updateCounters()}},1e3)},r.stop=function(){t.cancel(timer)},r.reset=function(){r.stop(),r.counter=r.initVal,r.progress=1},r.startTimer=function(t,o){r.current=o,r.stop(),r.counter=60*t,r.initVal=60*t,r.progress=1,r.start()},r.updateCounters=function(){"pomodoro"===r.current?r.takenPomodoros++:"short-break"===r.current?r.takenShortBreaks++:"long-break"===r.current&&r.takenLongBreaks++}}]),app.filter("timeFilter",function(){return function(r){var t=parseInt(r/60);return r%=60,r=10>r?"0"+r:r,t=10>t?"0"+t:t,t+":"+r}});